mapper.select:
SELECT fbi.fir_mst_sn AS store_id, -- 고유 번호 fbi.business_mark AS store_name, -- 매장명 fbi.industry AS industry, -- 업종명 ROUND(AVG(asr.avg_sales * 1000 / 12))::BIGINT AS avg_monthly_sales, -- 평균 월매출 (만원 단위) ROUND(bc.total * 1000)::BIGINT AS franchise_fee -- 가맹 부담금 FROM burden_costs AS bc JOIN franchise_basic_info AS fbi ON bc.fir_mst_sn = fbi.fir_mst_sn JOIN avg_sales_by_region AS asr ON fbi.fir_mst_sn = asr.fir_mst_sn WHERE bc.total IS NOT NULL AND bc.total > 0 AND asr.avg_sales IS NOT NULL AND asr.region = #{region} -- 유저 선택 가능 (지역)
AND fbi.industry = #{industry} -- 유저 선택 가능 (업종, 전체일 경우 제외)
GROUP BY fbi.fir_mst_sn, fbi.business_mark, fbi.industry, bc.total HAVING ROUND(AVG(asr.avg_sales * 1000 / 12))::BIGINT > 0 ORDER BY franchise_fee ASC LIMIT #{limit};
